    submit = () => {
        {props.users.map(user => (
        confirmAlert({
          title: 'Confirmer',
          message: 'suppression',
          buttons: [
            {
              label: 'Oui',
              onClick: () => {props.deleteUser(user.id)}
            },
            {
              label: 'Non',
            }
          ]
        })
        )
    )}
      };

      render(){
            return (
                <div>
                <button className="btn btn-danger" onClick={this.submit}>X</button>
                </div>
            );
      }

}


